import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import bakedImage from '../assets/BAKED.webp'; // Pastikan jalur ini sesuai dengan lokasi file Anda
import Footer from "./Footer"; // Import Footer

const CoffeeList = () => {
  const [coffeeBeans, setCoffeeBeans] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    async function fetchData() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeBeans(beans);
    }
    fetchData();
  }, []);

  // Filter kopi berdasarkan nama
  const filteredCoffeeBeans = coffeeBeans.filter(coffee =>
    coffee.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="max-w-full mx-0 p-0"> {/* Mengatur lebar maksimal penuh dan menghilangkan padding/margin */}
      <img 
        src={bakedImage} 
        alt="Baked Coffee" 
        className="w-full h-auto mt-0" // Pastikan tidak ada margin atas
      />
      <div className="max-w-6xl mx-auto p-6"> {/* Kontainer utama untuk konten lainnya */}
        <h1 className="text-3xl font-bold mb-4 font-permanent-marker text-center">Coffee Beans List</h1> {/* Tambahkan text-center */}
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="p-2 border border-gray-300 rounded mb-4 w-full max-w-xs"
        />
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4"> {/* Menggunakan grid untuk menampilkan kopi */}
          {filteredCoffeeBeans.map((coffee) => (
            <Link to={`/coffee/${coffee.id}`} key={coffee.id} className="block p-4 border border-gray-300 rounded hover:shadow-lg transition-shadow duration-200">
              <img src={coffee.image_url} alt={coffee.name} className="w-full h-auto rounded mb-2" />
              <h3 className="text-lg font-medium font-permanent-marker">{coffee.name}</h3>
              <p className="text-gray-600">${coffee.price.toFixed(2)}</p>
            </Link>
          ))}
        </div>
      </div>
      <Footer /> {/* Menambahkan komponen Footer */}
    </div>
  );
};

export default CoffeeList;
