// src/components/Home.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import beansImage from '../assets/beans.jpg';
import Footer from './Footer'; // Import the Footer component

const Home = () => {
  const [coffeeList, setCoffeeList] = useState([]);

  useEffect(() => {
    async function fetchCoffeeList() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeList(beans);
    }
    fetchCoffeeList();
  }, []);

  return (
    
    <div className="p-5 w-full box-border"> {/* Full width and padding */}
      <img src={beansImage} alt="Coffee Beans" className="w-full h-auto mb-5" /> {/* Full width image */}
      <h1 className="text-center text-gray-800 text-3xl mb-5">Welcome to the Coffee Shop</h1> {/* Title */}
      <div className="flex overflow-x-auto py-2"> {/* Horizontal scrolling container */}
        {coffeeList.map((coffee) => (
          <Link key={coffee.id} to={`/coffee/${coffee.id}`} className="flex flex-col items-center text-gray-800 border border-gray-300 rounded-lg p-3 mr-4 flex-shrink-0 transition-transform transform hover:-translate-y-1 hover:shadow-lg">
            <img src={coffee.image_url} alt={coffee.name} className="w-36 h-auto rounded-lg mb-2" /> {/* Coffee image */}
            <span className="text-lg font-bold text-center">{coffee.name}</span> {/* Coffee name */}
          </Link>
        ))}
      </div>
      <Footer /> {/* Add the Footer component here */}
    </div>
  );
};

export default Home;
