// src/components/Footer.js
import React from 'react';
import styled from 'styled-components';

const FooterContainer = styled.footer`
  background: #f8f8f8; /* Warna latar belakang */
  padding: 20px 0;
  text-align: center;
  margin-top: 20px; /* Jarak atas dari konten sebelumnya */
`;

const FooterText = styled.p`
  color: #333;
  margin: 0;
  font-size: 14px;
`;

const Footer = () => {
  return (
    <FooterContainer>
      <FooterText>&copy; {new Date().getFullYear()} Mr. Beans. All Rights Reserved.</FooterText>
    </FooterContainer>
  );
};

export default Footer;
