// src/components/CoffeeList.js
import React, { useState, useEffect } from "react";
import CoffeeItem from "./CoffeeItem";
import CoffeeDetail from "./CoffeeDetail";
import { CoffeeAPIService } from "../apiService";

const CoffeeList = () => {
  const [coffeeBeans, setCoffeeBeans] = useState([]);
  const [selectedCoffee, setSelectedCoffee] = useState(null);

  useEffect(() => {
    async function fetchData() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeBeans(beans);
    }
    fetchData();
  }, []);

  const handleCoffeeClick = (coffee) => {
    setSelectedCoffee(coffee);
  };

  return (
    <div>
      <h1>Coffee Beans List</h1>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {coffeeBeans.map((coffee) => (
          <CoffeeItem key={coffee.id} coffee={coffee} onClick={handleCoffeeClick} />
        ))}
      </div>
      <CoffeeDetail coffee={selectedCoffee} />
    </div>
  );
};

export default CoffeeList;
