// src/component/CoffeeDetail.js
import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";

const CoffeeDetail = ({ addToCart }) => {
  const { id } = useParams();
  const [coffee, setCoffee] = useState(null);
  const [coffeeList, setCoffeeList] = useState([]);
  const [showNotification, setShowNotification] = useState(false); // State for notification

  useEffect(() => {
    async function fetchCoffeeDetail() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      const selectedCoffee = beans.find((coffee) => coffee.id === parseInt(id));
      setCoffee(selectedCoffee);
      setCoffeeList(beans);
    }
    fetchCoffeeDetail();
  }, [id]);

  if (!coffee) return <p>Loading...</p>;

  // Handle add to cart and show notification
  const handleAddToCart = () => {
    addToCart(coffee); // Call the function to add the item to the cart
    setShowNotification(true); // Show notification
    setTimeout(() => setShowNotification(false), 3000); // Hide after 3 seconds
  };

  const sameRegionCoffees = coffeeList.filter(
    (coffeeItem) => coffeeItem.region === coffee.region && coffeeItem.id !== coffee.id
  );

  return (
    <div className="p-5 max-w-7xl mx-auto rounded-lg bg-white">
      <div className="flex items-start mb-5">
        <img
          src={coffee.image_url}
          alt={coffee.name}
          className="w-full h-96 rounded-lg mr-2"
        />
        <div>
          <h2 className="text-2xl font-medium text-gray-800 font-permanent-marker">{coffee.name}</h2>
          <p className="text-lg text-gray-600 mb-3">{coffee.description}</p>
          <p className="text-gray-700">Region: {coffee.region}</p>
          <p className="text-gray-700">Weight: {coffee.weight}g</p>
          <p className="text-gray-700">Price: ${coffee.price}</p>
          <p className="text-gray-700">Roast Level: {coffee.roast_level}</p>
          <button 
            onClick={handleAddToCart} 
            className="mt-4 px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 transition duration-300"
          >
            Add to Cart
          </button>
        </div>
      </div>

      {/* Notification Message */}
      {showNotification && (
        <div className="fixed bottom-4 right-4 bg-green-500 text-white p-4 rounded shadow-lg">
          Item added to cart!
        </div>
      )}

      <h4 className="text-2xl font-medium mb-4 text-center font-permanent-marker">More Coffees from the Same Region:</h4>
      <div className="flex flex-wrap justify-center gap-4">
        {sameRegionCoffees.map((coffeeItem) => (
          <Link
            key={coffeeItem.id}
            to={`/coffee/${coffeeItem.id}`}
            className="bg-white border border-gray-300 rounded-lg shadow-md overflow-hidden transition-transform transform hover:scale-105"
          >
            <img
              src={coffeeItem.image_url}
              alt={coffeeItem.name}
              className="w-full h-48 object-cover"
            />
            <span className="p-4 text-center font-medium text-lg text-gray-800 font-permanent-marker">{coffeeItem.name}</span>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default CoffeeDetail;
