// src/components/Profile.js
import React, { useEffect, useState } from 'react';

const Profile = () => {
  const [profileData, setProfileData] = useState(null);
  const username = 'ReyhanZidany'; // Ensure this is in quotes

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch(`https://api.github.com/users/${username}`);
        const data = await response.json();
        setProfileData(data);
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    fetchProfile();
  }, [username]);

  if (!profileData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="max-w-md mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">{profileData.name}</h1>
      <img src={profileData.avatar_url} alt={profileData.name} className="rounded-full mb-4 w-32 h-32" />
      <p className="text-gray-700"><strong>Bio:</strong> {profileData.bio || 'No bio available'}</p>
      <p className="text-gray-700"><strong>Location:</strong> {profileData.location || 'Not specified'}</p>
      <p className="text-gray-700"><strong>Followers:</strong> {profileData.followers}</p>
      <p className="text-gray-700"><strong>Following:</strong> {profileData.following}</p>
      <p className="text-gray-700"><strong>Public Repos:</strong> {profileData.public_repos}</p>
      <a href={profileData.html_url} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline">View on GitHub</a>
    </div>
  );
};

export default Profile;
