// src/component/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/mrbeans.png';
import { FaShoppingCart, FaBars } from 'react-icons/fa';

const Navbar = ({ cartItemCount }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <nav className="fixed bottom-0 left-0 w-full bg-white shadow-lg z-10 md:top-0 md:bottom-auto">
      <div className="flex justify-between items-center p-4 md:justify-center">
        {/* Logo - Visible on Desktop */}
        <Link to="/" className="hidden md:block">
          <img src={logo} alt="Bean Roast Logo" className="h-10 cursor-pointer" />
        </Link>

        {/* Mobile Menu Button */}
        <button
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          className="text-gray-800 md:hidden"
        >
          <FaBars className="h-6 w-6" />
        </button>

        {/* Desktop Links */}
        <div className="hidden md:flex items-center space-x-6">
          <Link to="/" className="text-gray-800 font-medium font-permanent-marker hover:underline">Home</Link>
          <Link to="/about" className="text-gray-800 font-medium font-permanent-marker hover:underline">About</Link>
          <Link to="/coffees" className="text-gray-800 font-medium font-permanent-marker hover:underline">Menu</Link>
          <Link to="/profile" className="text-gray-800 font-medium font-permanent-marker hover:underline">Profile</Link>
        </div>

        {/* Cart icon with item count */}
        <Link to="/cart" className="relative">
          <FaShoppingCart className="text-gray-800 h-6 w-6" />
          {cartItemCount > 0 && (
            <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full px-1">
              {cartItemCount}
            </span>
          )}
        </Link>
      </div>

      {/* Mobile Links - Displayed when Menu is Open */}
      {isMenuOpen && (
        <div className="flex flex-col items-center bg-gray-100 py-2 md:hidden">
          <Link
            to="/"
            onClick={() => setIsMenuOpen(false)}
            className="text-gray-800 font-medium font-permanent-marker hover:underline py-1"
          >
            Home
          </Link>
          <Link
            to="/about"
            onClick={() => setIsMenuOpen(false)}
            className="text-gray-800 font-medium font-permanent-marker hover:underline py-1"
          >
            About
          </Link>
          <Link
            to="/coffees"
            onClick={() => setIsMenuOpen(false)}
            className="text-gray-800 font-medium font-permanent-marker hover:underline py-1"
          >
            Menu
          </Link>
          <Link
            to="/profile"
            onClick={() => setIsMenuOpen(false)}
            className="text-gray-800 font-medium font-permanent-marker hover:underline py-1"
          >
            Profile
          </Link>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
