// src/component/CoffeeList.js
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService"; // Ensure this file exists
import { CoffeeFactory } from "../coffeeFactory"; // Ensure this file exists
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/swiper-bundle.min.css"; // Correct CSS import for Swiper
import { Navigation, Pagination } from "swiper"; // Import Navigation and Pagination modules
import styled from "styled-components";

// Styled components for styling
const ListContainer = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: auto;
`;

const CoffeeItemContainer = styled.div`
  padding: 16px;
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
`;

const CoffeeImage = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 5px;
  margin-bottom: 8px;
`;

const CoffeeName = styled.h3`
  margin: 0;
`;

const CoffeePrice = styled.p`
  margin: 0;
  font-weight: bold;
`;

const CoffeeList = () => {
  const [coffeeBeans, setCoffeeBeans] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const beansData = await CoffeeAPIService.fetchCoffeeBeans();
      const beans = beansData.map(CoffeeFactory.createCoffee);
      setCoffeeBeans(beans);
    }
    fetchData();
  }, []);

  return (
    <ListContainer>
      <h1>Coffee Beans Slider</h1>
      <Swiper
        spaceBetween={20}
        slidesPerView={3} // Adjust the number of visible slides
        pagination={{ clickable: true }}
        navigation
        modules={[Navigation, Pagination]} // Specify the modules here
      >
        {coffeeBeans.map((coffee) => (
          <SwiperSlide key={coffee.id}>
            <Link to={`/coffee/${coffee.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <CoffeeItemContainer>
                <CoffeeImage src={coffee.image_url} alt={coffee.name} />
                <CoffeeName>{coffee.name}</CoffeeName>
                <CoffeePrice>${coffee.price}</CoffeePrice>
              </CoffeeItemContainer>
            </Link>
          </SwiperSlide>
        ))}
      </Swiper>
    </ListContainer>
  );
};

export default CoffeeList;
