// src/components/Home.js
import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import beansImage from '../assets/roast.jpg';
import Footer from './Footer'; 

const Home = () => {
  const [coffeeList, setCoffeeList] = useState([]);
  const scrollContainerRef = useRef(null); 

  useEffect(() => {
    async function fetchCoffeeList() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeList(beans);
    }
    fetchCoffeeList();
  }, []);

  useEffect(() => {
    const scrollContainer = scrollContainerRef.current;

    const scroll = () => {
      if (scrollContainer) {
        scrollContainer.scrollBy({
          left: 1, 
          behavior: 'smooth'
        });
      }
    };

    const intervalId = setInterval(scroll, 30); 

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="flex flex-col min-h-screen"> 
      <div className="flex-grow max-w-full mx-0 p-0"> 
        <img src={beansImage} alt="Coffee Beans" className="w-full h-auto mb-5" /> {/* Full width image */}
        <h1 className="text-3xl font-medium mb-4 font-permanent-marker text-center">Welcome to Mr.Beans</h1> {/* Title */}
        <div ref={scrollContainerRef} className="flex overflow-hidden py-2"> 
          {coffeeList.map((coffee) => (
            <Link key={coffee.id} to={`/coffee/${coffee.id}`} className="flex flex-col items-center text-gray-800 border border-gray-300 rounded-lg p-3 mr-4 flex-shrink-0 transition-transform transform hover:-translate-y-1 hover:shadow-lg">
              <img 
                src={coffee.image_url} 
                alt={coffee.name} 
                className="w-36 h-36 object-cover rounded-lg mb-2" 
              />
              <span className="text-xl font-medium mb-4 text-center font-permanent-marker">{coffee.name}</span> 
            </Link>
          ))}
        </div>
      </div>
      <Footer /> {/* Add the Footer component here */}
    </div>
  );
};

export default Home;
