// src/component/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import { CoffeeAPIService } from "../apiService";
import beansImage from '../assets/roast.jpg';
import Footer from './Footer';

const Home = ({ onAddToCart }) => {
  const handleAddToCart = (coffee) => {
    // Logika untuk menambahkan coffee ke keranjang
    // Misalnya, menyimpan di local storage, state, atau konteks
    // Setelah item ditambahkan, panggil fungsi onAddToCart
    onAddToCart(); // Memanggil fungsi untuk mengupdate cartItemCount
  };

  // Kode lainnya...

  return (
    <div className="flex flex-col min-h-screen">
      <div className="flex-grow max-w-full mx-0 p-0">
        <img src={beansImage} alt="Coffee Beans" className="w-full h-auto mb-5" />
        <h1 className="text-3xl font-bold mb-4 font-permanent-marker text-center">Welcome to Mr.Beans</h1>
        <div className="flex overflow-hidden py-2">
          {coffeeList.map((coffee) => (
            <Link 
              key={coffee.id} 
              to={`/coffee/${coffee.id}`} 
              className="flex flex-col items-center text-gray-800 border border-gray-300 rounded-lg p-3 mr-4 flex-shrink-0 transition-transform transform hover:-translate-y-1 hover:shadow-lg"
            >
              <img 
                src={coffee.image_url} 
                alt={coffee.name} 
                className="w-36 h-36 object-cover rounded-lg mb-2"
              />
              <span className="text-xl font-medium mb-4 text-center font-permanent-marker">{coffee.name}</span>
              {/* Tombol untuk menambahkan ke keranjang */}
              <button onClick={() => handleAddToCart(coffee)} className="bg-green-500 text-white px-2 py-1 rounded">
                Add to Cart
              </button>
            </Link>
          ))}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
