// src/components/CoffeeDetail.js
import React from "react";
import styled from "styled-components";

const DetailContainer = styled.div`
  border: 1px solid #ddd;
  padding: 16px;
  margin: 8px;
`;

const CoffeeDetail = ({ coffee }) => {
  if (!coffee) return <p>Select a coffee to see details.</p>;

  return (
    <DetailContainer>
      <h2>{coffee.name}</h2>
      <p>{coffee.description}</p>
      <p>Region: {coffee.region}</p>
      <p>Weight: {coffee.weight}g</p>
      <p>Roast Level: {coffee.roast_level}</p>
      <h4>Flavor Profile:</h4>
      <ul>
        {coffee.flavor_profile.map((flavor) => (
          <li key={flavor}>{flavor}</li>
        ))}
      </ul>
    </DetailContainer>
  );
};

export default CoffeeDetail;
