// src/components/CoffeeDetail.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import styled from "styled-components";

const DetailContainer = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: auto;
`;

const CoffeeImage = styled.img`
  width: 200px;
  height: auto;
  margin-bottom: 20px;
  border-radius: 5px;
`;

const CoffeeDetail = () => {
  const { id } = useParams();
  const [coffee, setCoffee] = useState(null);

  useEffect(() => {
    async function fetchCoffeeDetail() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      const selectedCoffee = beans.find((coffee) => coffee.id === parseInt(id));
      setCoffee(selectedCoffee);
    }
    fetchCoffeeDetail();
  }, [id]);

  if (!coffee) return <p>Loading...</p>;

  return (
    <DetailContainer>
      <CoffeeImage src={coffee.image_url} alt={coffee.name} />
      <h2>{coffee.name}</h2>
      <p>{coffee.description}</p>
      <p>Region: {coffee.region}</p>
      <p>Weight: {coffee.weight}g</p>
      <p>Price: ${coffee.price}</p>
      <p>Roast Level: {coffee.roast_level}</p>
      <h4>Flavor Profile:</h4>
      <ul>
        {coffee.flavor_profile.map((flavor) => (
          <li key={flavor}>{flavor}</li>
        ))}
      </ul>
      <h4>Grind Options:</h4>
      <ul>
        {coffee.grind_option.map((option) => (
          <li key={option}>{option}</li>
        ))}
      </ul>
    </DetailContainer>
  );
};

export default CoffeeDetail;
