import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import styled from "styled-components";

const ListContainer = styled.div`
  padding: 20px;
  max-width: 1200px; /* Lebar maksimum untuk kontainer */
  margin: auto;
`;

const SearchInput = styled.input`
  padding: 10px;
  width: 100%; /* Lebar penuh */
  max-width: 400px; /* Lebar maksimum input pencarian */
  margin-bottom: 20px; /* Jarak bawah input */
  border: 1px solid #ddd;
  border-radius: 5px;
`;

const CoffeeGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr)); /* Membuat grid responsif dengan kolom minimal 300px */
  gap: 16px; /* Jarak antar item */
`;

const CoffeeItemContainer = styled.div`
  border: 1px solid #ddd;
  padding: 16px;
  display: flex;
  flex-direction: column; /* Mengatur item menjadi kolom */
  align-items: center; /* Mengatur item agar rata tengah */
  transition: box-shadow 0.3s;
  cursor: pointer;

  &:hover {
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2); /* Efek bayangan saat hover */
  }
`;

const CoffeeImage = styled.img`
  width: 100%; /* Gambar memenuhi lebar item */
  height: auto; /* Tinggi otomatis untuk menjaga proporsi */
  border-radius: 5px;
  margin-bottom: 10px; /* Jarak bawah gambar */
`;

const CoffeeName = styled.h3`
  margin: 0; /* Menghapus margin default */
  font-size: 1.2em; /* Ukuran font lebih besar */
`;

const CoffeePrice = styled.p`
  font-size: 1em; /* Ukuran font harga */
  color: #555; /* Warna abu-abu untuk harga */
`;

const CoffeeList = () => {
  const [coffeeBeans, setCoffeeBeans] = useState([]);
  const [searchTerm, setSearchTerm] = useState(""); // State untuk teks pencarian

  useEffect(() => {
    async function fetchData() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeBeans(beans);
    }
    fetchData();
  }, []);

  // Filter kopi berdasarkan nama
  const filteredCoffeeBeans = coffeeBeans.filter(coffee =>
    coffee.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <ListContainer>
      <h1>Coffee Beans List</h1>
      <SearchInput
        type="text"
        placeholder="Search by name..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)} // Mengupdate state saat ada perubahan
      />
      <CoffeeGrid>
        {filteredCoffeeBeans.map((coffee) => (
          <Link to={`/coffee/${coffee.id}`} key={coffee.id} style={{ textDecoration: 'none', color: 'inherit' }}>
            <CoffeeItemContainer>
              <CoffeeImage src={coffee.image_url} alt={coffee.name} />
              <CoffeeName>{coffee.name}</CoffeeName>
              <CoffeePrice>${coffee.price.toFixed(2)}</CoffeePrice>
            </CoffeeItemContainer>
          </Link>
        ))}
      </CoffeeGrid>
    </ListContainer>
  );
};

export default CoffeeList;
