// src/components/Home.js
import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import beansImage from '../assets/roast.jpg';
import Footer from './Footer'; // Import the Footer component

const Home = () => {
  const [coffeeList, setCoffeeList] = useState([]);
  const scrollContainerRef = useRef(null); // Ref for the scroll container

  useEffect(() => {
    async function fetchCoffeeList() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeList(beans);
    }
    fetchCoffeeList();
  }, []);

  useEffect(() => {
    const scrollContainer = scrollContainerRef.current;

    // Function to scroll the container
    const scroll = () => {
      if (scrollContainer) {
        scrollContainer.scrollBy({
          left: 1, // Adjust this value for scroll speed
          behavior: 'smooth'
        });
      }
    };

    const intervalId = setInterval(scroll, 30); // Adjust interval time for smoothness

    // Clean up the interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="max-w-full mx-0 p-0"> {/* Full width and padding */}
      <img src={beansImage} alt="Coffee Beans" className="w-full h-auto mb-5" /> {/* Full width image */}
      <h1 className="text-3xl font-bold mb-4 font-permanent-marker text-center">Welcome to Mr.Beans</h1> {/* Title */}
      <div ref={scrollContainerRef} className="flex overflow-hidden py-2"> {/* Horizontal scrolling container */}
        {coffeeList.map((coffee) => (
          <Link key={coffee.id} to={`/coffee/${coffee.id}`} className="flex flex-col items-center text-gray-800 border border-gray-300 rounded-lg p-3 mr-4 flex-shrink-0 transition-transform transform hover:-translate-y-1 hover:shadow-lg">
            <img src={coffee.image_url} alt={coffee.name} className="w-36 h-auto rounded-lg mb-2" /> {/* Coffee image */}
            <span className="text-lg font-bold text-center">{coffee.name}</span> {/* Coffee name */}
          </Link>
        ))}
      </div>
      <Footer /> {/* Add the Footer component here */}
    </div>
  );
};

export default Home;
