// src/components/Footer.js
import React from 'react';
import styled from 'styled-components';
import paperImage from '../assets/paper.jpg'; // Import the paper.jpg image

const FooterContainer = styled.footer`
  background: url(${paperImage}) no-repeat center center; /* Use the image as background */
  background-size: cover; /* Cover the entire footer */
  padding: 20px 0;
  text-align: center;
  margin-top: 20px; /* Space from the previous content */
`;

const FooterText = styled.p`
  color: #333; /* Dark text for contrast */
  margin: 0;
  font-size: 14px;
  text-shadow: 1px 1px 2px rgba(255, 255, 255, 0.7); /* Optional: Add text shadow for better readability */
`;

const Footer = () => {
  return (
    <FooterContainer>a
      <FooterText>&copy; {new Date().getFullYear()} Coffee Shop. All Rights Reserved.</FooterText>
    </FooterContainer>
  );
};

export default Footer;
