// src/component/Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/mrbeans.png'; 
import { FaShoppingCart } from 'react-icons/fa'; 

const Navbar = ({ cartItemCount }) => {
  return (
    <nav className="flex justify-between items-center bg-white p-4 shadow-md fixed w-full z-10">
      <Link to="/">
        <img src={logo} alt="Bean Roast Logo" className="h-10 cursor-pointer" /> {/* Adjust height as needed */}
      </Link>
      <div className="flex items-center space-x-6">
        <Link to="/" className="text-gray-800 font-bold font-permanent-marker hover:underline">Home</Link>
        <Link to="/about" className="text-gray-800 font-bold font-permanent-marker hover:underline">About</Link>
        <Link to="/coffees" className="text-gray-800 font-bold font-permanent-marker hover:underline">Menu</Link>
        <Link to="/profile" className="text-gray-800 font-bold font-permanent-marker hover:underline">Profile</Link>
        
        {/* Cart icon with item count */}
        <Link to="/cart" className="relative">
          <FaShoppingCart className="text-gray-800 h-6 w-6" />
          {cartItemCount > 0 && (
            <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full px-1">
              {cartItemCount}
            </span>
          )}
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
