// src/components/About.js
import React from 'react';
import coffeeImage from '../assets/roast.jpg'; // Import a relevant image

const About = () => {
  return (
    <div className="max-w-4xl mx-auto p-6 text-center bg-gray-50 rounded-lg shadow-md mt-10">
      <h1 className="text-4xl font-bold mb-6 text-gray-800">About Us</h1>
      <img src={coffeeImage} alt="Coffee Beans" className="w-full h-64 object-cover rounded-lg mb-6" />
      <p className="mb-4 text-gray-700 text-lg">
        Welcome to our Coffee Beans website! We are passionate about providing you with the finest selection of coffee beans from around the world.
      </p>
      <p className="mb-4 text-gray-700 text-lg">
        Our mission is to connect coffee lovers with high-quality coffee products and educate them about the different varieties of coffee.
      </p>
      <p className="text-gray-700 text-lg">
        Explore our Coffee List to discover new flavors and aromas!
      </p>
      <a href="/coffees" className="mt-6 inline-block bg-green-600 text-white font-bold py-2 px-4 rounded hover:bg-black-700 transition duration-300">
        View Coffee List
      </a>
    </div>
  );
};

export default About;
