// src/components/Home.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import beansImage from '../assets/beans.jpg';
import styled from "styled-components";

const HomeContainer = styled.div`
  padding: 20px;
  max-width: 1700px; /* Increase width for larger screens */
  margin: auto;
`;

const BeansImage = styled.img`
  width: 100%; /* Full width for the image */
  height: auto; /* Maintain aspect ratio */
  margin-bottom: 20px; /* Space below image */
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
  margin-bottom: 20px; /* Space below title */
`;

const CoffeeListContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr)); /* Responsive grid */
  gap: 20px; /* Space between items */
`;

const CoffeeListItem = styled(Link)`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-decoration: none;
  color: #333; /* Darker text color */
  border: 1px solid #ddd; /* Light border for card effect */
  border-radius: 8px; /* Rounded corners */
  padding: 10px;
  transition: transform 0.2s, box-shadow 0.2s; /* Smooth transition */
  
  &:hover {
    transform: translateY(-5px); /* Slight lift effect */
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1); /* Shadow on hover */
  }
`;

const CoffeeListImage = styled.img`
  width: 100%; /* Full width for image */
  height: auto;
  border-radius: 8px; /* Consistent rounding */
  margin-bottom: 5px;
`;

const CoffeeListText = styled.span`
  font-size: 1.1em; /* Slightly larger font size */
  text-align: center;
  font-weight: bold; /* Bold text for names */
`;

const Home = () => {
  const [coffeeList, setCoffeeList] = useState([]);

  useEffect(() => {
    async function fetchCoffeeList() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeList(beans);
    }
    fetchCoffeeList();
  }, []);

  return (
    <HomeContainer>
      <div className="w-full">
        <BeansImage src={beansImage} alt="Coffee Beans" className="w-full h-auto mt-0" />
      </div>
      <Title>Welcome to the Coffee Shop</Title>
      <CoffeeListContainer>
        {coffeeList.map((coffee) => (
          <CoffeeListItem key={coffee.id} to={`/coffee/${coffee.id}`}>
            <CoffeeListImage src={coffee.image_url} alt={coffee.name} />
            <CoffeeListText>{coffee.name}</CoffeeListText>
          </CoffeeListItem>
        ))}
      </CoffeeListContainer>
    </HomeContainer>
  );
};

export default Home;
