// src/components/Profile.js
import React, { useEffect, useState } from 'react';
import Footer from './Footer';
import axios from 'axios';

const Profile = () => {
  const [profileData, setProfileData] = useState(null);
  const [isOffline, setIsOffline] = useState(!navigator.onLine);
  const username = 'ReyhanZidany';

  useEffect(() => {
    // Check online/offline status
    const handleOffline = () => setIsOffline(true);
    const handleOnline = () => setIsOffline(false);

    window.addEventListener('offline', handleOffline);
    window.addEventListener('online', handleOnline);

    return () => {
      window.removeEventListener('offline', handleOffline);
      window.removeEventListener('online', handleOnline);
    };
  }, []);

  useEffect(() => {
    const fetchProfile = async () => {
      if (!window.caches) return; // Check if Cache API is supported

      const cache = await caches.open('profile-cache');
      const cacheUrl = `https://api.github.com/users/${username}`;

      try {
        // Attempt to fetch from API
        const response = await axios.get(cacheUrl);
        setProfileData(response.data);

        // Store response in cache
        const clonedResponse = new Response(JSON.stringify(response.data), {
          headers: { 'Content-Type': 'application/json' }
        });
        cache.put(cacheUrl, clonedResponse);
      } catch (error) {
        console.error("Network error:", error);

        // If offline, retrieve data from cache
        if (isOffline) {
          const cachedResponse = await cache.match(cacheUrl);
          if (cachedResponse) {
            const cachedData = await cachedResponse.json();
            setProfileData(cachedData);
          } else {
            console.log("No cached data available");
          }
        }
      }
    };

    fetchProfile();
  }, [username, isOffline]);

  if (!profileData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="flex flex-col min-h-screen">
      <div className="max-w-md mx-auto p-6 text-center flex flex-col items-center justify-center flex-grow">
        <img 
          src={profileData.avatar_url} 
          alt={profileData.name} 
          className="rounded-full mb-4 w-32 h-32" 
        />
        <h1 className="text-2xl font-bold mb-2">{profileData.name}</h1>
        <a 
          href={profileData.html_url} 
          target="_blank" 
          rel="noopener noreferrer" 
          className="text-blue-500 hover:underline"
        >
          Visit GitHub Profile
        </a>
      </div>

      <Footer />
    </div>
  );
};

export default Profile;
