// src/components/CoffeeDetail.js
import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import { FaShoppingCart } from "react-icons/fa"; 

const CoffeeDetail = ({ cartItems = [], setCartItems }) => {  // Default `cartItems` to an empty array
  const { id } = useParams();
  const [coffee, setCoffee] = useState(null);
  const [coffeeList, setCoffeeList] = useState([]);
  const [showNotification, setShowNotification] = useState(false);

  useEffect(() => {
    async function fetchCoffeeDetail() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      const selectedCoffee = beans.find((coffee) => coffee.id === parseInt(id));
      setCoffee(selectedCoffee);
      setCoffeeList(beans);
    }
    fetchCoffeeDetail();
  }, [id]);

  if (!coffee) return <p>Loading...</p>;

  const handleAddToCart = () => {
    const existingItem = cartItems.find((item) => item.id === coffee.id);
    if (existingItem) {
      setCartItems(
        cartItems.map((item) =>
          item.id === coffee.id ? { ...item, quantity: item.quantity + 1 } : item
        )
      );
    } else {
      setCartItems([...cartItems, { ...coffee, quantity: 1 }]);
    }

    setShowNotification(true); 
    setTimeout(() => setShowNotification(false), 3000);
  };

  return (
    <div className="p-5 max-w-7xl mx-auto rounded-lg bg-white">
      <div className="flex items-start mb-5">
        {/* Coffee details */}
      </div>

      {showNotification && (
        <div className="fixed bottom-4 right-4 bg-green-500 text-white p-4 rounded shadow-lg">
          Item added to cart!
        </div>
      )}

      <h4 className="text-2xl font-medium mb-4 text-center font-permanent-marker">
        More Coffees from the Same Region:
      </h4>

      <div className="flex flex-wrap justify-center gap-4">
        {/* Other coffees */}
      </div>
    </div>
  );
};

export default CoffeeDetail;
