// src/components/Footer.js
import React from 'react';
import paperImage from '../assets/paper.jpg'; // Import the paper.jpg image

const Footer = () => {
  return (
    <footer className="bg-cover bg-center py-5 text-center w-full" style={{ backgroundImage: `url(${paperImage})` }}> {/* Background image */}
      <p className="text-gray-800 text-sm m-0 text-shadow"> {/* Footer text */}
        &copy; {new Date().getFullYear()} Coffee Shop. All Rights Reserved.
      </p>
    </footer>
  );
};

export default Footer;
