import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import bakedImage from '../assets/BAKED.webp'; // Pastikan jalur ini sesuai dengan lokasi file Anda

const CoffeeList = () => {
  const [coffeeBeans, setCoffeeBeans] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    async function fetchData() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeBeans(beans);
    }
    fetchData();
  }, []);

  // Mengelompokkan kopi berdasarkan region
  const groupByRegion = (beans) => {
    return beans.reduce((groups, coffee) => {
      const region = coffee.region || "Unknown"; // Ganti 'region' dengan field yang sesuai
      if (!groups[region]) {
        groups[region] = [];
      }
      groups[region].push(coffee);
      return groups;
    }, {});
  };

  // Filter kopi berdasarkan nama
  const filteredCoffeeBeans = coffeeBeans.filter(coffee =>
    coffee.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Kelompokkan kopi yang sudah difilter
  const groupedCoffeeBeans = groupByRegion(filteredCoffeeBeans);

  return (
    <div className="max-w-full mx-0 p-0">
      <div className="w-full">
        <img 
          src={bakedImage} 
          alt="Baked Coffee" 
          className="w-full h-auto mt-0" // Atur margin atas ke 0 untuk menghilangkan jarak
        />
      </div>
      <h1 className="text-3xl font-bold mb-4 font-permanent-marker text-center">Coffee Beans List</h1>
      <input
        type="text"
        placeholder="Search by name..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="p-2 border border-gray-300 rounded mb-4 w-full max-w-xs"
      />
      {Object.keys(groupedCoffeeBeans).map(region => (
        <div key={region} className="mb-8">
          <h2 className="text-2xl font-semibold mb-2 font-permanent-marker">{region}</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {groupedCoffeeBeans[region].map((coffee) => (
              <Link to={`/coffee/${coffee.id}`} key={coffee.id} className="block p-4 border border-gray-300 rounded hover:shadow-lg transition-shadow duration-200">
                <img src={coffee.image_url} alt={coffee.name} className="w-full h-auto rounded mb-2" />
                <h3 className="text-lg font-medium font-permanent-marker">{coffee.name}</h3>
                <p className="text-gray-600">${coffee.price.toFixed(2)}</p>
              </Link>
            ))}
          </div>
        </div>
      ))}
      {/* Menambahkan gambar BAKED.webp di bawah daftar kopi */}
    </div>
  );
};

export default CoffeeList;
