const CoffeeDetail = () => {
  const { id } = useParams();
  const [coffee, setCoffee] = useState(null);
  const [coffeeList, setCoffeeList] = useState([]);

  useEffect(() => {
    async function fetchCoffeeDetail() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      const selectedCoffee = beans.find((coffee) => coffee.id === parseInt(id));
      setCoffee(selectedCoffee);
      setCoffeeList(beans); // Set the entire coffee list here
    }
    fetchCoffeeDetail();
  }, [id]);

  if (!coffee) return <p>Loading...</p>;

  // Filter the coffee list to show only the same region
  const sameRegionCoffees = coffeeList.filter((coffeeItem) => coffeeItem.region === coffee.region && coffeeItem.id !== coffee.id);

  return (
    <DetailContainer>
      <CoffeeDetailContent>
        <CoffeeImage src={coffee.image_url} alt={coffee.name} />
        <div>
          <CoffeeTitle>{coffee.name}</CoffeeTitle>
          <CoffeeDescription>{coffee.description}</CoffeeDescription>
          <CoffeeDetailItem>Region: {coffee.region}</CoffeeDetailItem>
          <CoffeeDetailItem>Weight: {coffee.weight}g</CoffeeDetailItem>
          <CoffeeDetailItem>Price: ${coffee.price}</CoffeeDetailItem>
          <CoffeeDetailItem>Roast Level: {coffee.roast_level}</CoffeeDetailItem>
          <DetailHeader>Flavor Profile:</DetailHeader>
          <FlavorList>
            {coffee.flavor_profile.map((flavor) => (
              <li key={flavor}>{flavor}</li>
            ))}
          </FlavorList>
          <DetailHeader>Grind Options:</DetailHeader>
          <FlavorList>
            {coffee.grind_option.map((option) => (
              <li key={option}>{option}</li>
            ))}
          </FlavorList>
        </div>
      </CoffeeDetailContent>

      <DetailHeader>More Coffees from the Same Region:</DetailHeader>
      <CoffeeListContainer>
        {sameRegionCoffees.map((coffeeItem) => (
          <CoffeeListItem key={coffeeItem.id} to={`/coffee/${coffeeItem.id}`}>
            <CoffeeListImage src={coffeeItem.image_url} alt={coffeeItem.name} />
            <CoffeeListText>{coffeeItem.name}</CoffeeListText>
          </CoffeeListItem>
        ))}
      </CoffeeListContainer>
    </DetailContainer>
  );
};
