// src/components/CoffeeDetail.js
import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";

const CoffeeDetail = () => {
  const { id } = useParams();
  const [coffee, setCoffee] = useState(null);
  const [coffeeList, setCoffeeList] = useState([]);

  useEffect(() => {
    async function fetchCoffeeDetail() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      const selectedCoffee = beans.find((coffee) => coffee.id === parseInt(id));
      setCoffee(selectedCoffee);
      setCoffeeList(beans);
    }
    fetchCoffeeDetail();
  }, [id]);

  if (!coffee) return <p>Loading...</p>;

  // Filter the coffee list to show only the same region
  const sameRegionCoffees = coffeeList.filter((coffeeItem) => coffeeItem.region === coffee.region && coffeeItem.id !== coffee.id);

  return (
    <div className="p-5 max-w-5xl mx-auto rounded-lg bg-white">
      <div className="flex items-start mb-5">
        <img
          src={coffee.image_url}
          alt={coffee.name}
          className="w-full h-96 rounded-lg mr-2"
        />
        <div>
          <h2 className="text-2xl font-bold text-gray-800 font-permanent-marker">{coffee.name}</h2> {/* Mengubah font */}
          <p className="text-lg text-gray-600 mb-3">{coffee.description}</p>
          <p className="text-gray-700">Region: {coffee.region}</p>
          <p className="text-gray-700">Weight: {coffee.weight}g</p>
          <p className="text-gray-700">Price: ${coffee.price}</p>
          <p className="text-gray-700">Roast Level: {coffee.roast_level}</p>
          <h4 className="mt-5 text-lg font-semibold text-gray-800">Flavor Profile:</h4>
          <ul className="list-disc list-inside pl-5 text-gray-700">
            {coffee.flavor_profile.map((flavor) => (
              <li key={flavor}>{flavor}</li>
            ))}
          </ul>
          <h4 className="mt-5 text-lg font-semibold text-gray-800">Grind Options:</h4>
          <ul className="list-disc list-inside pl-5 text-gray-700">
            {coffee.grind_option.map((option) => (
              <li key={option}>{option}</li>
            ))}
          </ul>
        </div>
      </div>

      <h4 className="text-2xl font-semibold mb-4 text-center font-permanent-marker">More Coffees from the Same Region:</h4> {/* Mengubah font */}

      <div className="flex flex-wrap justify-center gap-4">
        {sameRegionCoffees.map((coffeeItem) => (
          <Link
            key={coffeeItem.id}
            to={`/coffee/${coffeeItem.id}`}
            className="bg-white border border-gray-300 rounded-lg shadow-md overflow-hidden transition-transform transform hover:scale-105"
          >
            <img
              src={coffeeItem.image_url}
              alt={coffeeItem.name}
              className="w-full h-48 object-cover"
            />
            <span className="p-4 text-center font-medium text-lg text-gray-800 font-permanent-marker">{coffeeItem.name}</span>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default CoffeeDetail;
