import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import CoffeeList from "./component/CoffeeList";
import CoffeeDetail from "./component/CoffeeDetail";
import Profile from './component/Profile';
import Navbar from "./component/Navbar"; 
import About from './component/About';
import Home from './component/Home';
import Cart from "./component/Cart";
import SplashScreen from './components/SplashScreen'; // Import splash screen

const App = () => {
  const [isLoading, setIsLoading] = useState(true); // State untuk splash screen

  useEffect(() => {
    // Atur durasi splash screen selama 2 detik
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2000); // 2000ms = 2 detik

    return () => clearTimeout(timer);
  }, []);

  const [cartItems, setCartItems] = useState([]);

  const addToCart = (coffee) => {
    setCartItems((prevItems) => [...prevItems, coffee]);
  };

  const clearCart = () => {
    setCartItems([]);
  };

  return (
    <Router>
      {isLoading ? (
        <SplashScreen /> // Tampilkan splash screen selama state isLoading true
      ) : (
        <div className="min-h-screen flex flex-col">
          <Navbar cartItemCount={cartItems.length} />
          <main className="flex-grow pt-16">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/coffees" element={<CoffeeList />} />
              <Route path="/about" element={<About />} />
              <Route 
                path="/coffee/:id" 
                element={<CoffeeDetail addToCart={addToCart} />} 
              />
              <Route path="/profile" element={<Profile />} /> 
              <Route 
                path="/cart" 
                element={<Cart cartItems={cartItems} clearCart={clearCart} />} 
              />
            </Routes>
          </main>
        </div>
      )}
    </Router>
  );
};

export default App;
