// src/components/Home.js
import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import beansImage from '../assets/roast1.jpg';
import Footer from './Footer';
import { saveCoffeeBeans, getCachedCoffeeBeans } from '../utils/indexedDB';

const Home = () => {
  const [coffeeList, setCoffeeList] = useState([]);
  const [isOffline, setIsOffline] = useState(!navigator.onLine);  // Check initial online status
  const scrollContainerRef = useRef(null);

  // Monitor online/offline status
  useEffect(() => {
    const handleOffline = () => setIsOffline(true);
    const handleOnline = () => setIsOffline(false);

    window.addEventListener('offline', handleOffline);
    window.addEventListener('online', handleOnline);

    return () => {
      window.removeEventListener('offline', handleOffline);
      window.removeEventListener('online', handleOnline);
    };
  }, []);

  // Fetch coffee list with IndexedDB caching logic
  useEffect(() => {
    async function fetchCoffeeList() {
      if (!navigator.onLine) {
        // If the user is offline, load from IndexedDB
        const cachedBeans = await getCachedCoffeeBeans();
        if (cachedBeans && cachedBeans.length > 0) {
          setCoffeeList(cachedBeans);
        } else {
          // Optionally, show a message that there's no data available offline
          console.log("No cached data available, and offline.");
        }
      } else {
        // When online, fetch from the API
        try {
          const beans = await CoffeeAPIService.fetchCoffeeBeans();
          setCoffeeList(beans);

          // Save fetched data to IndexedDB for offline access
          await saveCoffeeBeans(beans);
        } catch (error) {
          console.error("Error fetching data from API:", error);

          // If there's a network error, try to use cached data
          if (!navigator.onLine) {
            const cachedBeans = await getCachedCoffeeBeans();
            if (cachedBeans && cachedBeans.length > 0) {
              setCoffeeList(cachedBeans);
            }
          }
        }
      }
    }

    fetchCoffeeList();
  }, [isOffline]);  // Re-run when online/offline status changes

  // Auto-scrolling for the coffee items
  useEffect(() => {
    const scrollContainer = scrollContainerRef.current;

    const scroll = () => {
      if (scrollContainer) {
        scrollContainer.scrollBy({
          left: 0.5,
          behavior: 'smooth'
        });
      }
    };

    const intervalId = setInterval(scroll, 50);
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      <header className="w-full">
        <img 
          src={beansImage} 
          alt="Coffee Beans" 
          className="w-full h-auto mb-5 md:h-64 lg:h-80 object-cover" 
        />
      </header>

      <main className="flex-grow max-w-full mx-auto px-4">
        <h1 className="text-2xl sm:text-3xl md:text-4xl font-medium font-permanent-marker text-center my-4 text-brown-900">
          Welcome to Mr.Beans
        </h1>
        <p className="text-center text-gray-700 text-lg max-w-2xl mx-auto mb-6">
          Discover a curated selection of coffee beans from around the world. Each one is unique and offers a taste of quality and tradition.
        </p>

        <div 
          ref={scrollContainerRef} 
          className="flex overflow-hidden py-2 mb-6"
          style={{ scrollBehavior: "smooth" }}
        >
          {coffeeList.map((coffee) => (
            <Link 
              key={coffee.id} 
              to={`/coffee/${coffee.id}`} 
              className="flex flex-col items-center text-gray-800 border border-gray-300 rounded-lg p-3 mr-4 flex-shrink-0 transition-transform transform hover:-translate-y-1 hover:shadow-lg"
            >
              <img 
                src={coffee.image_url} 
                alt={coffee.name} 
                className="w-36 h-36 sm:w-44 sm:h-44 md:w-48 md:h-48 object-cover rounded-lg mb-2" 
              />
              <span className="text-xl font-medium mb-4 text-center font-permanent-marker">
                {coffee.name}
              </span>
            </Link>
          ))}
        </div>
      </main>

      <Footer />
    </div>
  );
};

export default Home;
