// src/components/CoffeeList.js
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import styled from "styled-components";

const ListContainer = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: auto;
`;

const CoffeeItemContainer = styled.div`
  border: 1px solid #ddd;
  padding: 16px;
  margin: 8px 0;
  display: flex;
  align-items: center;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #f9f9f9;
  }
`;

const CoffeeImage = styled.img`
  width: 60px;
  height: 60px;
  margin-right: 16px;
  border-radius: 5px;
`;

const CoffeeList = () => {
  const [coffeeBeans, setCoffeeBeans] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeBeans(beans);
    }
    fetchData();
  }, []);

  return (
    <ListContainer>
      <h1>Coffee Beans List</h1>
      {coffeeBeans.map((coffee) => (
        <Link to={`/coffee/${coffee.id}`} key={coffee.id} style={{ textDecoration: 'none', color: 'inherit' }}>
          <CoffeeItemContainer>
            <CoffeeImage src={coffee.image_url} alt={coffee.name} />
            <div>
              <h3>{coffee.name}</h3>
              <p>${coffee.price}</p>
            </div>
          </CoffeeItemContainer>
        </Link>
      ))}
    </ListContainer>
  );
};

export default CoffeeList;
