// src/component/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/mrbeans.png';
import { FaShoppingCart, FaBars, FaTimes } from 'react-icons/fa';

const Navbar = ({ cartItemCount }) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <nav className="bg-white p-4 shadow-md fixed w-full z-10">
      <div className="flex justify-between items-center">
        {/* Logo */}
        <Link to="/">
          <img src={logo} alt="Bean Roast Logo" className="h-10 cursor-pointer" />
        </Link>

        {/* Hamburger button on mobile */}
        <button onClick={toggleSidebar} className="text-gray-800 md:hidden">
          <FaBars className="h-6 w-6" />
        </button>

        {/* Desktop menu */}
        <div className="hidden md:flex items-center space-x-6">
          <Link to="/" className="text-gray-800 font-medium font-permanent-marker hover:underline">Home</Link>
          <Link to="/about" className="text-gray-800 font-medium font-permanent-marker hover:underline">About</Link>
          <Link to="/coffees" className="text-gray-800 font-medium font-permanent-marker hover:underline">Menu</Link>
          <Link to="/profile" className="text-gray-800 font-medium font-permanent-marker hover:underline">Profile</Link>
          
          {/* Cart icon with item count */}
          <Link to="/cart" className="relative">
            <FaShoppingCart className="text-gray-800 h-6 w-6" />
            {cartItemCount > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full px-1">
                {cartItemCount}
              </span>
            )}
          </Link>
        </div>
      </div>

      {/* Sidebar menu for mobile */}
      {isSidebarOpen && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 z-20">
          <div className="fixed top-0 left-0 w-64 h-full bg-white shadow-lg p-4">
            <button onClick={toggleSidebar} className="text-gray-800 mb-4">
              <FaTimes className="h-6 w-6" />
            </button>
            <Link to="/" onClick={toggleSidebar} className="block text-gray-800 font-medium font-permanent-marker py-2">Home</Link>
            <Link to="/about" onClick={toggleSidebar} className="block text-gray-800 font-medium font-permanent-marker py-2">About</Link>
            <Link to="/coffees" onClick={toggleSidebar} className="block text-gray-800 font-medium font-permanent-marker py-2">Menu</Link>
            <Link to="/profile" onClick={toggleSidebar} className="block text-gray-800 font-medium font-permanent-marker py-2">Profile</Link>
            <Link to="/cart" onClick={toggleSidebar} className="block text-gray-800 font-medium font-permanent-marker py-2">
              Cart
              {cartItemCount > 0 && (
                <span className="ml-2 bg-red-500 text-white text-xs rounded-full px-2">
                  {cartItemCount}
                </span>
              )}
            </Link>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
