// src/components/Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import logo from '../assets/mrbean.webp'; // Pastikan jalur ini sesuai dengan lokasi file Anda
import bakedImage from '../assets/BAKED.webp'; // Pastikan jalur ini sesuai dengan lokasi file Anda

const NavbarContainer = styled.nav`
  display: flex;
  justify-content: space-between; /* Menyusun logo di kiri dan link di kanan */
  align-items: center;
  background: white; /* Ubah latar belakang navbar menjadi putih */
  padding: 10px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1); /* Tambahkan bayangan untuk efek kedalaman */
`;

const Logo = styled.img`
  height: 40px; /* Ukuran tinggi logo */
  cursor: pointer;
`;

const NavLink = styled(Link)`
  color: #333; /* Ubah warna teks menjadi gelap untuk kontras dengan latar belakang putih */
  margin-left: 20px;
  text-decoration: none;
  font-weight: bold;
  font-family: 'Permanent Marker', cursive; /* Menambahkan font Permanent Marker */

  &:hover {
    text-decoration: underline;
  }
`;

const BakedImage = styled.img`
  width: 100%; /* Gambar di bawah navbar akan mengisi lebar penuh */
  height: auto; /* Tinggi gambar otomatis untuk mempertahankan rasio */
  max-height: 500px; /* Atur tinggi maksimum gambar jika diperlukan */
  display: block; /* Pastikan tidak ada margin di bawah gambar */
`;

const Navbar = () => {
  return (
    <>
      <NavbarContainer>
        <Link to="/">
          <Logo src={logo} alt="Bean Roast Logo" />
        </Link>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <NavLink to="/">Home</NavLink>
          <NavLink to="/about">About</NavLink>
          <NavLink to="/coffee">Coffee List</NavLink>
          <NavLink to="/contact">Contact</NavLink>
        </div>
      </NavbarContainer>
      <BakedImage src={bakedImage} alt="Baked Coffee" />
    </>
  );
};

export default Navbar;
