// src/component/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/mrbeans.png'; // Pastikan path ini sesuai dengan lokasi file
import { FaShoppingCart } from 'react-icons/fa'; // Import ikon keranjang belanja dari react-icons

const Navbar = ({ cartItemCount }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State untuk mengelola menu mobile

  return (
    <nav className="flex justify-between items-center bg-white p-4 shadow-md fixed w-full z-10">
      <Link to="/">
        <img src={logo} alt="Bean Roast Logo" className="h-10 cursor-pointer" /> {/* Logo Mr. Beans */}
      </Link>

      {/* Tombol Menu untuk Mobile */}
      <button className="lg:hidden" onClick={() => setIsMenuOpen(!isMenuOpen)}>
        <span className="material-icons">menu</span> {/* Ganti ini dengan ikon yang sesuai jika menggunakan react-icons */}
      </button>

      {/* Menu Mobile */}
      {isMenuOpen && (
        <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg z-10">
          <Link to="/about" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">About</Link>
          <Link to="/coffees" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">Menu</Link>
          <Link to="/profile" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">Profile</Link>
        </div>
      )}

      {/* Cart icon dengan item count */}
      <Link to="/cart" className="hidden lg:block relative"> {/* Tampilkan ikon keranjang belanja di desktop saja */}
        <FaShoppingCart className="text-gray-800 h-6 w-6" />
        {cartItemCount > 0 && (
          <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full px-1">
            {cartItemCount}
          </span>
        )}
      </Link>
    </nav>
  );
};

export default Navbar;
