// src/components/CoffeeList.js
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService";
import bakedImage from '../assets/header3.jpg';
import Footer from "./Footer";
import Slider from "react-slick"; // Import Slider from react-slick
import "slick-carousel/slick/slick.css"; // Import slick CSS
import "slick-carousel/slick/slick-theme.css"; // Import slick theme CSS

const CoffeeList = () => {
  const [coffeeBeans, setCoffeeBeans] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    async function fetchData() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeBeans(beans);
    }
    fetchData();
  }, []);

  const groupByRegion = (beans) => {
    return beans.reduce((groups, coffee) => {
      const region = coffee.region || "Unknown";
      if (!groups[region]) {
        groups[region] = [];
      }
      groups[region].push(coffee);
      return groups;
    }, {});
  };

  const filteredCoffeeBeans = coffeeBeans.filter(coffee =>
    coffee.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const groupedCoffeeBeans = groupByRegion(filteredCoffeeBeans);

  // Slider settings
  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  // Custom next arrow component
  const SampleNextArrow = (props) => {
    const { className, style, onClick } = props;
    return (
      <div
        className={`${className} absolute top-1/2 right-2 transform -translate-y-1/2 text-black`}
        style={{ ...style, display: "block", zIndex: 1 }}
        onClick={onClick}
      />
    );
  };

  // Custom previous arrow component
  const SamplePrevArrow = (props) => {
    const { className, style, onClick } = props;
    return (
      <div
        className={`${className} absolute top-1/2 left-2 transform -translate-y-1/2 text-black`}
        style={{ ...style, display: "block", zIndex: 1 }}
        onClick={onClick}
      />
    );
  };

  return (
    <div className="max-w-full mx-0 p-0">
      <div className="w-full">
        <img src={bakedImage} alt="Baked Coffee" className="w-full h-80 object-cover mb-5" />
      </div>
      <h1 className="text-3xl font-bold mb-4 font-permanent-marker text-center">Coffee Beans List</h1>
      <input
        type="text"
        placeholder="Search by name..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="p-2 border border-gray-300 rounded mb-4 w-full max-w-xs mx-auto"
      />
      {Object.keys(groupedCoffeeBeans).map(region => (
        <div key={region} className="mb-8">
          <h2 className="text-2xl font-semibold mb-2 font-permanent-marker text-center">{region}</h2>
          <Slider {...settings} className="mb-8">
            {groupedCoffeeBeans[region].map((coffee) => (
              <Link to={`/coffee/${coffee.id}`} key={coffee.id} className="block p-4 border border-gray-300 rounded hover:shadow-lg transition-shadow duration-200">
                <img src={coffee.image_url} alt={coffee.name} className="w-full h-auto rounded mb-2" />
                <h3 className="text-lg font-medium font-permanent-marker">{coffee.name}</h3>
                <p className="text-gray-600">${coffee.price.toFixed(2)}</p>
              </Link>
            ))}
          </Slider>
        </div>
      ))}
      <Footer />
    </div>
  );
};

export default CoffeeList;
