// src/components/Home.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { CoffeeAPIService } from "../apiService"; // Pastikan ini sesuai dengan struktur API Anda
import styled from "styled-components";

const HomeContainer = styled.div`
  padding: 20px;
  max-width: 800px;
  margin: auto;
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
`;

const CoffeeListContainer = styled.div`
  display: flex;
  flex-direction: column; /* Arrange items vertically */
  align-items: center; /* Center items */
`;

const CoffeeListItem = styled(Link)`
  display: flex;
  flex-direction: column; /* Arrange items vertically */
  align-items: center; /* Center items */
  margin: 10px; /* Add margin around each item */
  text-decoration: none;
  color: #007bff;
`;

const CoffeeListImage = styled.img`
  width: 100px; /* Set the image size */
  height: auto;
  border-radius: 5px;
  margin-bottom: 5px; /* Space between image and text */
`;

const CoffeeListText = styled.span`
  font-size: 1em; /* Adjust font size for the text */
  text-align: center; /* Center text */
`;

const Home = () => {
  const [coffeeList, setCoffeeList] = useState([]);

  useEffect(() => {
    async function fetchCoffeeList() {
      const beans = await CoffeeAPIService.fetchCoffeeBeans();
      setCoffeeList(beans);
    }
    fetchCoffeeList();
  }, []);

  return (
    <HomeContainer>
      <Title>Welcome to the Coffee Shop</Title>
      <CoffeeListContainer>
        {coffeeList.map((coffee) => (
          <CoffeeListItem key={coffee.id} to={`/coffee/${coffee.id}`}>
            <CoffeeListImage src={coffee.image_url} alt={coffee.name} />
            <CoffeeListText>{coffee.name}</CoffeeListText>
          </CoffeeListItem>
        ))}
      </CoffeeListContainer>
    </HomeContainer>
  );
};

export default Home;
