// src/component/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/mrbeans.png'; // Ensure this path matches your file location
import { FaShoppingCart } from 'react-icons/fa'; // Import cart icon from react-icons

const Navbar = ({ cartItemCount }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State to manage mobile menu

  return (
    <nav className="flex justify-between items-center bg-white p-4 shadow-md fixed w-full z-10">
      <Link to="/">
        <img src={logo} alt="Bean Roast Logo" className="h-10 cursor-pointer" /> {/* Adjust height as needed */}
      </Link>
      <div className="flex items-center space-x-6 lg:space-x-8"> {/* Adjust space for larger screens */}
        <Link to="/" className="text-gray-800 font-bold font-permanent-marker hover:underline">Home</Link>
        <Link to="/about" className="text-gray-800 font-bold font-permanent-marker hover:underline">About</Link>
        <Link to="/coffees" className="text-gray-800 font-bold font-permanent-marker hover:underline">Menu</Link>
        <Link to="/profile" className="text-gray-800 font-bold font-permanent-marker hover:underline">Profile</Link>
        
        {/* Cart icon with item count */}
        <Link to="/cart" className="relative">
          <FaShoppingCart className="text-gray-800 h-6 w-6" />
          {cartItemCount > 0 && (
            <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full px-1">
              {cartItemCount}
            </span>
          )}
        </Link>
      </div>
      
      {/* Mobile Menu Button */}
      <button className="lg:hidden" onClick={() => setIsMenuOpen(!isMenuOpen)}>
        <span className="material-icons">menu</span>
      </button>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg z-10">
          <Link to="/" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">Home</Link>
          <Link to="/about" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">About</Link>
          <Link to="/coffees" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">Menu</Link>
          <Link to="/profile" className="block px-4 py-2 text-gray-800 hover:bg-gray-100">Profile</Link>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
