// src/components/CoffeeItem.js
import React from "react";
import styled from "styled-components";

const CoffeeItemContainer = styled.div`
  cursor: pointer;
  border: 1px solid #ddd;
  padding: 16px;
  margin: 8px;
  display: flex;
  align-items: center;
`;

const CoffeeImage = styled.img`
  width: 50px;
  height: 50px;
  margin-right: 16px;
`;

const CoffeeItem = ({ coffee, onClick }) => {
  return (
    <CoffeeItemContainer onClick={() => onClick(coffee)}>
      <CoffeeImage src={coffee.image_url} alt={coffee.name} />
      <div>
        <h3>{coffee.name}</h3>
        <p>${coffee.price}</p>
      </div>
    </CoffeeItemContainer>
  );
};

export default CoffeeItem;
